sources.styles/main.scss:
@import "utils";
@import "fonts";
@import "typography";
@import "block/container.scss";
@import "block/about-us.scss";
@import "block/title.scss";
@import "block/gallery.scss";
@import "block/footer.scss";

* {
  box-sizing: border-box;
}

html {
  font-family: "Roboto", sans-serif;
  font-weight: 700;
  font-size: 16px;
}

h1 {
  margin: 0;
}

body {
  margin: 0;
  background: $c-gray;
}

.header {
  height: 100vh;
  padding: 24px 0 80px;
  background-image: url(../images/header.jpg);
  background-size: cover;
  background-position: center;

  &__body {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
  }
  &__top {
    display: flex;
    justify-content: space-between;
    //position: relative;
    height: 130px;
    &__burger {
      &__label {
        display: block;
        background-image: url(../images/icons/Icon-Burger-menu.svg);
        background-position: center;
        width: 32px;
        height: 32px;
        &:hover {
          background-image: url(../images/icons/Icon-Burger-menu-hover.svg);
        }
      }

      &__menu-list {
        position: absolute;
        top: -1000%;
        left: 0;
        z-index: 1;
        width: 100vw;
        min-height: 100vh;
        background-color: blue;
        transition-duration: 0.3s;
        margin: 0;
        padding-top: 145px;
      }
      &__toggle {
        display: none;
        &:checked ~ .header__top__burger__label {
          position: relative;
          z-index: 2;
          background-image: url(../images/icons/Icon-Close.svg);
          background-position: center;
          width: 32px;
          height: 32px;
        }
        &:checked ~ .header__top__burger__menu-list {
          visibility: visible;
          top: 0;
        }
      }
    }
    &__logo {
      display: block;
      position: relative;
      z-index: 2;
      width: 136px;
      height: 80px;
    }

    &__call {
      height: 32px;
      width: 32px;
      padding-top: 8px;
      background-image: url(../images/icons/Icon-Phone-call.svg);
      background-repeat: no-repeat;
      background-position: right;
      &:hover::before {
        content: "+1 234 555-55-55";
        position: absolute;
        right: 0;
        margin-right: 48px;
        color: #fff;
        font-size: 12px;
        //что б не сразу пропадал номер
        transition-duration: 0.5s;
      }
    }
  }
  &__title {
    font-size: 80px;
    font-weight: 700;
    color: #fff;
    -webkit-text-stroke: 1px black;
    -webkit-text-fill-color: white;
    &::before {
      content: "";
      display: block;
      height: 2px;
      width: 4%;
      border-radius: 1px;
      background-color: #000;
    }
  }

  &__item {
    list-style: none;
  }

  &__link {
    display: block;
    padding: 22px 0;
    color: #fff;
    border-radius: 15px;
    font-weight: 700;
    font-size: 22px;
    text-decoration: none;
    text-transform: uppercase;
    transition-duration: 0.3s;
  }

  &__menu-list-bottom {
    margin-top: 45px;
    font-size: 12px;
    font-weight: 700;
    color: #fff;
  }
}
