sources.styles/block/about-us.scss:
.about-us {
  display: flex;
  justify-content: space-between;
  &__column {
    width: 50%;
    padding-top: 100px;
    margin-bottom: 150px;
    &__name {
      font-size: 48px;
      font-weight: 700;
    }
    &--blue {
      color: blue;
    }
    &__title {
      font-size: 24px;
      padding-left: 10px;
    }
    &__text {
      font-size: 16px;
      font-weight: 400;
      padding-left: 10px;
    }
    &__advantages {
      padding-left: 10px;
    }
  }
}

sources.styles/block/title.scss:
.title {
  font-size: 48px;
  font-weight: 700;
  text-transform: uppercase;
  text-align: center;
  &::after {
    content: "";
    display: block;
    height: 24px;
    width: 24px;
    margin: 0 auto;
    background-image: url(./../images/crown.svg);
    background-repeat: no-repeat;
  }
}

sources.styles/block/gallery.scss:
.gallery {
  &__title {
    margin-bottom: 30px;
  }
  &__row {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 310px;
    justify-content: center;
    gap: 0 20px;
    margin-bottom: 40px;
    &--75-25 {
      grid-template-columns: 2fr 1fr;
    }
    &--50-50 {
      grid-template-columns: 1fr 1fr;
    }
    &--25-75 {
      grid-template-columns: 1fr 2fr;
    }
  }
  &__img {
    height: 100%;
    width: 100%;
    object-fit: cover;
  }
  &__subtitle {
    margin-top: 24px;
    font-size: 16px;
    font-weight: 400;
    &--link {
      color: blue;
      font-weight: 700;
      text-decoration: none;
    }
  }
  &__view-all {
    display: block;
    width: 154px;
    height: 56px;
    margin: 0 auto 156px;
    font-size: 16px;
    color: #0075ff;
    border: 1px solid  #0075ff;
    border-radius: 5px;
    text-align: center;
    text-decoration: none;
    padding: 19px 0;
  }
}

